import React, { useState, useEffect } from 'react';
import WelcomeScreen from './components/WelcomeScreen';
import HomeScreen from './components/screens/HomeScreen';
import ExploreScreen from './components/screens/ExploreScreen';
import StoresScreen from './components/screens/StoresScreen';

// Create a simple ProfileScreen component directly in this file to avoid import issues
const ProfileScreen = () => {
  return (
    <div className="px-4 py-4">
      <h2 className="text-xl font-bold mb-4">Profile</h2>
      <p className="text-white/60 mb-4">This is a simple profile page.</p>

      <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4 mb-4">
        <div className="flex items-center mb-4">
          <div className="w-16 h-16 rounded-full bg-gradient-to-r from-[#FF2E63] to-[#00F5FF] flex items-center justify-center mr-4">
            <img 
              src="https://readdy.ai/api/search-image?query=Professional%20portrait%20of%20a%20stylish%20young%20woman%20with%20short%20dark%20hair%2C%20natural%20makeup%2C%20fashion%20model%20aesthetic%2C%20high%20quality%2C%20photorealistic%2C%20soft%20lighting%2C%20studio%20shot%2C%20isolated%20on%20dark%20background%2C%20centered%20composition&width=100&height=100&seq=user1&orientation=squarish"
              alt="Profile"
              className="w-14 h-14 rounded-full object-cover"
            />
          </div>
          <div>
            <h3 className="text-lg font-semibold">Sophia Chen</h3>
            <p className="text-[#00F5FF] text-sm">@sophiastyle</p>
          </div>
        </div>

        <p className="text-white/80 mb-4">Fashion enthusiast | Fitness lover | Always on the lookout for the next trend</p>

        <div className="flex justify-between mb-4">
          <div className="text-center">
            <p className="text-lg font-bold bg-gradient-to-r from-[#FF2E63] to-[#00F5FF] bg-clip-text text-transparent">42</p>
            <p className="text-xs text-white/60">Posts</p>
          </div>
          <div className="text-center">
            <p className="text-lg font-bold bg-gradient-to-r from-[#FF2E63] to-[#00F5FF] bg-clip-text text-transparent">1248</p>
            <p className="text-xs text-white/60">Followers</p>
          </div>
          <div className="text-center">
            <p className="text-lg font-bold bg-gradient-to-r from-[#FF2E63] to-[#00F5FF] bg-clip-text text-transparent">356</p>
            <p className="text-xs text-white/60">Following</p>
          </div>
        </div>

        <button className="w-full bg-gradient-to-r from-[#FF2E63] to-[#00F5FF] text-white py-2 rounded-full text-sm font-medium">
          Edit Profile
        </button>
      </div>

      <div className="bg-white/10 backdrop-blur-sm rounded-xl p-4">
        <h3 className="text-lg font-semibold mb-3">Recent Activity</h3>
        <div className="flex items-center p-3 rounded-xl bg-white/5 backdrop-blur-sm mb-3">
          <div className="w-12 h-12 rounded-xl overflow-hidden mr-3 bg-white/10">
            <img src="https://readdy.ai/api/search-image?query=Stylish%20oversized%20hoodie%20in%20neon%20colors%2C%20fashion%20item%20on%20dark%20background%2C%20product%20photography%2C%20high%20quality&width=80&height=80&seq=item1&orientation=squarish" alt="Item" className="w-full h-full object-cover" />
          </div>
          <div>
            <div className="flex items-center mb-1">
              <div className="w-5 h-5 rounded-full bg-[#00F5FF]/20 text-[#00F5FF] flex items-center justify-center mr-2">
                <i className="fas fa-shopping-bag text-xs"></i>
              </div>
              <span className="text-xs text-white/60">Purchased</span>
            </div>
            <h4 className="text-sm font-medium">Neon Oversized Hoodie</h4>
            <p className="text-xs text-white/40">2 days ago</p>
          </div>
        </div>
      </div>
    </div>
  );
};

const App = () => {

  const [activeTab, setActiveTab] = useState('profile');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulate loading time
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 2000);
    return () => clearTimeout(timer);
  }, []);

  if (isLoading) {
    return <WelcomeScreen onStart={() => setIsLoading(false)} />;
  }

  return (
    <div className="relative h-[762px] w-[375px] bg-[#121214] text-white overflow-hidden font-sans">
      {/* Nav Bar */}
      <div className="fixed top-0 w-full z-50 bg-[#121214]/80 backdrop-blur-md border-b border-white/10 px-4 py-3 flex justify-between items-center">
        <div className="text-xl font-bold bg-gradient-to-r from-[#FF2E63] to-[#00F5FF] bg-clip-text text-transparent">
          FlashFit
        </div>
        <div className="flex items-center space-x-3">
          <button className="w-8 h-8 rounded-full bg-white/10 flex items-center justify-center backdrop-blur-lg cursor-pointer">
            <i className="fas fa-bell text-sm text-white/80"></i>
          </button>
          <div className="w-8 h-8 rounded-full bg-gradient-to-r from-[#FF2E63] to-[#00F5FF] flex items-center justify-center cursor-pointer">
            <img
              src="https://readdy.ai/api/search-image?query=Professional%20portrait%20of%20a%20stylish%20young%20woman%20with%20short%20dark%20hair%2C%20natural%20makeup%2C%20fashion%20model%20aesthetic%2C%20high%20quality%2C%20photorealistic%2C%20soft%20lighting%2C%20studio%20shot%2C%20isolated%20on%20dark%20background%2C%20centered%20composition&width=100&height=100&seq=user1&orientation=squarish"
              alt="Profile"
              className="w-7 h-7 rounded-full object-cover"
            />
          </div>
        </div>
      </div>

      {/* Main Content Area - with padding to avoid nav and tab bar overlap */}
      <div className="pt-16 pb-20 h-full overflow-y-auto" style={{ msOverflowStyle: 'none', scrollbarWidth: 'none' }}>
        {activeTab === 'home' && <HomeScreen />}
        {activeTab === 'explore' && <ExploreScreen />}
        {activeTab === 'stores' && <StoresScreen />}
        {activeTab === 'profile' && <ProfileScreen />}
      </div>

      {/* Global style to hide scrollbars across the app */}
      <style dangerouslySetInnerHTML={{ __html: `
        /* Hide scrollbars but keep functionality */
        ::-webkit-scrollbar { width: 0; height: 0; }
        ::-webkit-scrollbar-track { background: transparent; }
        ::-webkit-scrollbar-thumb { background: transparent; }
        ::-webkit-scrollbar-button { display: none; }

        /* For Firefox */
        * { scrollbar-width: none; }

        /* For IE/Edge */
        * { -ms-overflow-style: none; }
      `}} />

      {/* Tab Bar */}
      <div className="fixed bottom-0 w-full z-50 bg-[#121214]/80 backdrop-blur-md border-t border-white/10">
        <div className="grid grid-cols-4 h-16">
          {[
            { id: 'home', icon: 'fa-home', label: 'Home' },
            { id: 'explore', icon: 'fa-search', label: 'Explore' },
            { id: 'stores', icon: 'fa-store', label: 'Stores' },
            { id: 'profile', icon: 'fa-user', label: 'Profile' }
          ].map(tab => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex flex-col items-center justify-center cursor-pointer ${activeTab === tab.id ? 'text-[#FF2E63]' : 'text-white/60'}`}
            >
              <i className={`fas ${tab.icon} ${activeTab === tab.id ? 'text-lg' : 'text-base'}`}></i>
              <span className="text-xs mt-1">{tab.label}</span>
              {activeTab === tab.id && (
                <div className="absolute bottom-0 w-6 h-1 bg-gradient-to-r from-[#FF2E63] to-[#00F5FF] rounded-t-full"></div>
              )}
            </button>
          ))}
        </div>
      </div>

      {/* Floating Action Button */}
      <button className="rounded-button fixed right-5 bottom-20 w-14 h-14 bg-gradient-to-r from-[#FF2E63] to-[#00F5FF] rounded-full flex items-center justify-center shadow-lg shadow-[#FF2E63]/20 cursor-pointer">
        <i className="fas fa-shopping-bag text-xl"></i>
      </button>
    </div>
  );
};

export default App;
